mapper.update:
update login
set IS_CURRENT = 2
where USER_ID = #{userId,jdbcType=INTEGER}
and DEVICE_INFO = #{deviceInfo,jdbcType=VARCHAR}
and IS_CURRENT = 1

mapper.select:
select 

from login
where LOGIN_TOKEN = #{loginToken,jdbcType=VARCHAR}
and DEVICE_INFO = #{deviceInfo,jdbcType=VARCHAR}
and IS_CURRENT = 1
