  MESSAGE2 = #{message2,jdbcType=VARCHAR},
  MESSAGE3 = #{message3,jdbcType=VARCHAR},
  MESSAGE4 = #{message4,jdbcType=VARCHAR},
  MESSAGE5 = #{message5,jdbcType=VARCHAR}
where NOTI_MSG_TYPE = #{notiMsgType,jdbcType=INTEGER}
  and LANGUAGE_CODE = #{languageCode,jdbcType=VARCHAR}
