mapper.update:
update dictionary
set DICTIONARY_TYPE = #{dictionaryType,jdbcType=INTEGER},
  DICTIONARY_CODE = #{dictionaryCode,jdbcType=INTEGER},
  DICTIONARY_NAME = #{dictionaryName,jdbcType=VARCHAR},
  DICTIONARY_ORDER = #{dictionaryOrder,jdbcType=INTEGER}
where DICTIONARY_ID = #{dictionaryId,jdbcType=INTEGER}

mapper.select:
select 

from dictionary
where DICTIONARY_TYPE = #{dictionaryType,jdbcType=INTEGER}
and DICTIONARY_ID = #{dictionaryId, jdbcType=INTEGER}
