mapper.update:
update works_tag
set WORKS_ID = #{worksId,jdbcType=INTEGER},
  TAG_NAME = #{tagName,jdbcType=VARCHAR},
  TAG_ORDER = #{tagOrder,jdbcType=INTEGER}
where WORKS_TAG_ID = #{worksTagId,jdbcType=INTEGER}

mapper.insert:
    insert into works_tag (WORKS_ID, TAG_NAME, TAG_ORDER)
    values

	   	#{element.worksId,jdbcType=INTEGER}, #{element.tagName,jdbcType=VARCHAR}, #{element.tagOrder,jdbcType=INTEGER}

update.foreach:
			update works_tag

				 WORKS_ID = #{element.worksId,jdbcType=INTEGER},
      			TAG_NAME = #{element.tagName,jdbcType=VARCHAR},
      			TAG_ORDER = #{element.tagOrder,jdbcType=INTEGER}

			where WORKS_TAG_ID = #{element.worksTagId,jdbcType=INTEGER}

mapper.delete:
		delete from works_tag
 	where WORKS_TAG_ID in 

 		#{item.worksTagId,jdbcType=INTEGER}

mapper.select:
select 

from works_tag
where WORKS_ID = #{worksId, jdbcType=INTEGER}

select (ifnull(max(TAG_ORDER),0) + 1) as num 
from works_tag 
where WORKS_ID = #{worksId, jdbcType=INTEGER}

select wk.WORKS_ID, wk.USER_ID, ur.USER_NAME, ur.PROFILE_PICTURE, wk.PRIVACY_TYPE, wk.DEVICE_TYPE, wk.TITLE, wk.DESCRIPTION, wk.IMAGE_PATH, wk.BDW_PATH, wk.LANGUAGE_CODE, wk.COUNTRY_CODE, wk.STATUS, wk.UPDATE_DATE, wl.WORKS_LIKE_ID, wl.WORKS_ID as LIKE_WORKS_ID, wl.USER_ID as LIKE_USER_ID, wl.LIKE_TYPE, ifnull(wl.LIKE_COUNT,0) as LIKE_COUNT, ifnull(wm.MSG_COUNT,0) as MSG_COUNT from works wk right join (select * from works_tag where tag_name in
#{element.tagName}
) wt on wt.works_id = wk.works_id left join user_info ur on ur.USER_ID = wk.USER_ID left join (select *, count(WORKS_ID) as LIKE_COUNT from works_like where like_type=1 group by WORKS_ID ) as wl on wl.WORKS_ID = wk.WORKS_ID left join (select *, count(WORKS_MSG_ID) as MSG_COUNT from works_msg group by WORKS_ID ) as wm on wm.WORKS_ID = wk.WORKS_ID where 1=1 and wk.PRIVACY_TYPE = 1 and wk.STATUS = 1 group by wk.WORKS_ID order by LIKE_COUNT desc, MSG_COUNT desc LIMIT 6
